        }
      } else {
        farm[i][0] = 'o';
        scanf("%s", &farm[i][1]);
        farm[i][W + 1] = 'o';
      }
    }
    ans = 0;
    for (i = 1; i < H + 1; i++) {
      for (j = 1; j < W + 1; j++) {
        if (farm[i][j] != 'o') {
          ans += bfs(i, j, farm, vector);
        }
      }
    }
    printf("%d\n", ans);
  }
  return 0;
}

int bfs(int h, int w, char farm[][102], int vector[][2]) {
  int i, j;
  char now = farm[h][w];
  farm[h][w] = 'o';
  for (i = 0; i < 4; i++) {
    if (now == farm[h + vector[i][0]][w + vector[i][1]]) {
      bfs(h + vector[i][0], w + vector[i][1], farm, vector);
    }
  }
  return 1;
}

