#include <string.h>

int main(void) {
  int num, kazu, poll[200000] = {0}, max = 0, many = 0;
  char names[200000][11], win[200000][11];
  scanf("%d", &num);

  for (int i = 0; i < num; i++) {
    scanf("%s", names[i]);
  }
  qsort(names, num, 11, strcmp);
  kazu = 0;
  strcpy(win[0], names[0]);

  for (int i = 1; i < num; i++) {
    if (strcmp(names[i], names[i - 1]) == 0) {
      poll[kazu]++;
    } else {
      kazu++;
      strcpy(win[kazu], names[i]);
    }
  }

  for (int i = 0; i <= kazu; i++) {
    if (poll[i] > max)
      max = poll[i];
  }

  for (int i = 0; i <= kazu; i++) {
    if (poll[i] == max)
      printf("%s\n", win[i]);
  }

  return 0;
}

