  for (int i = n - 3; i >= 0; i--) {
    int one_skip_cost, two_skip_cost;
    one_skip_cost = abs(heights[i + 1] - heights[i]) + memo[i + 1];
    two_skip_cost = abs(heights[i + 2] - heights[i]) + memo[i + 2];
    memo[i] = min(one_skip_cost, two_skip_cost);
